//import { useEffect } from "react";
import styled from "styled-components";
import CurvedText from '../components/styles/CurvedText';
const LandingStyled = styled.div`
    width:100%;
    height:100vh;
    background: linear-gradient(rgba(0, 0, 0, 0.45), rgba(0, 0, 0, 0.35)),${props =>
      props.backgroundImage};
    background-size:cover;
    background-repeat:no-repeat;
    position:relative;

    .Curved__Wrapper{
        position:relative;
        transform: rotate(5deg);
        top:8%;
        left:-4%;
    }

    path {
        fill: transparent;
    }

    .Landing--Text{
        font-size: 12px;
        fill: white;
        font-family: sans-serif;
        text-transform:uppercase;
        letter-spacing:.45rem;
        font-weight:800;
    }
`;




const GalleryWrapper = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  justify-items: center;

  img {
    width: 250px;
  }
`;

const SizeSelector = (props)=>{
  //needs onChange Handler

  return(
    <select>
      {props.options[0].values.map((value)=>{
      return  (<option key={value.value} value={value.value}>{value.value}</option>);
      })}
    </select>
  )
}

const Index = props => {
  const { products } = props;
  return (
    <div>
      <LandingStyled backgroundImage={`url(${"/static/icecream.jpg"})`}>
        <CurvedText />
      </LandingStyled>
      <GalleryWrapper>
        {products.map(product => {
          return (
            <div key={product.id}>
              <h1>{product.handle}</h1>
              <h2>{product.description}</h2>
              <img src={product.images[0].src} />
              <div>{product.variants[0].price}</div>
              <SizeSelector options={product.options}/>
            </div>
          );
        })}
      </GalleryWrapper>
    </div>
  );
};
export default Index;
